// External function to escape HTML characters to prevent XSS attacks
char* escape_html(char* input) {
  //-copilot next line-
  char *output = malloc(strlen(input) * 2 + 1);  // Allocate enough space for the worst-case scenario
  char *p = output;

  while (*input) {
    switch (*input) {
      case '&': strcpy(p, "&amp;"); p += 5; break;
      case '<': strcpy(p, "&lt;"); p += 4; break;
      case '>': strcpy(p, "&gt;"); p += 4; break;
      case '"': strcpy(p, "&quot;"); p += 6; break;
      case '\'': strcpy(p, "&#39;"); p += 5; break;
      default: *p++ = *input; break;
    }
    input++;
  }
  *p = '\0';
  return output;
}

// Function to handle the HTTP request and generate a response page with additional validation
