          PAGE_HEAD "<h1>Error: Username cannot be empty</h1>" PAGE_TAIL, response);
    return;
  }

  char *safe_username = escape_html(username);
  snprintf(outputbuf, MAX_OUTPUT_LENGTH, 
           "HTTP/1.1 200 OK\r\nContent-Type: text/html\r\n\r\n"
           PAGE_HEAD "<h1>Hello, %s!</h1>" PAGE_TAIL, safe_username);
  free(safe_username);

